title: Linux Server Manager Tool (No Dependencies)
author: AI Assistant
funding_url: https://github.com/your-repo
version: 1.0.0
license: MIT
description: Linux 서버 및 Kubernetes 클러스터 관리 도구 - 시스템 SSH 명령어 사용 (의존성 최소화)
"""

# ──────────────────────────
# 표준 라이브러리
# ──────────────────────────
import os
import json
import asyncio
import subprocess
import re
from datetime import datetime
from typing import Callable, Any, Dict, List
from pydantic import BaseModel, Field


# ──────────────────────────
# 공통 유틸리티
# ──────────────────────────
class HelpFunctions:
    """공통으로 사용되는 유틸리티 함수들"""

    def clean_text(self, text: str) -> str:
        """텍스트 정리 및 포맷팅"""
        if not text:
            return ""
        return re.sub(r"\s+", " ", text).strip()

    def format_datetime(self, dt: datetime | None = None) -> str:
        """날짜‧시간을 YYYY-MM-DD HH:MM:SS 형식으로 변환"""
        return (dt or datetime.now()).strftime("%Y-%m-%d %H:%M:%S")

    def sanitize_input(self, input_str: str) -> str:
        """명령어 인자를 안전하게 변환"""
        dangerous_chars = [";", "&", "|", "`", "$", "(", ")", "<", ">", '"', "'"]
        for char in dangerous_chars:
            input_str = input_str.replace(char, "")
        return input_str.strip()


# ──────────────────────────
# 진행 상황 이벤트 전송
# ──────────────────────────
class EventEmitter:
    """진행 상황 알림용"""

    def __init__(self, event_emitter: Callable[[dict], Any] | None = None):
        self.event_emitter = event_emitter

    async def emit(
        self, description: str, status: str = "in_progress", done: bool = False
    ):
        if self.event_emitter:
            await self.event_emitter(
                {
                    "type": "status",
                    "data": {
                        "status": status,
                        "description": description,
                        "done": done,
                    },
                }
            )

    async def emit_error(self, msg: str):
        await self.emit(f"오류 발생: {msg}", "error", True)

    async def emit_success(self, msg: str):
        await self.emit(msg, "complete", True)


# ──────────────────────────
# SSH 연결 관리 (ssh / sshpass 호출)
# ──────────────────────────
class SSHManager:
    """시스템 SSH 명령어 기반 원격 실행"""

    def __init__(
        self,
        host: str,
        port: int,
        username: str,
        password: str = "",
        key_path: str = "",
    ):
        self.host, self.port, self.username = host, port, username
        self.password, self.key_path = password, key_path
        self.connection_tested = False

    async def test_connection(self) -> bool:
        """간단한 echo 로 연결 가능 여부 확인"""
        cmd = self._build_ssh_command("echo connection_test")
        proc = await asyncio.create_subprocess_shell(
            cmd, stdout=asyncio.subprocess.PIPE, stderr=asyncio.subprocess.PIPE
        )
        stdout, _ = await proc.communicate()
        self.connection_tested = proc.returncode == 0 and b"connection_test" in stdout
        return self.connection_tested

    def _build_ssh_command(self, remote_cmd: str) -> str:
        """ssh/sshpass 실행 문자열 생성"""
        opts = [
            "-o",
            "StrictHostKeyChecking=no",
            "-o",
            "UserKnownHostsFile=/dev/null",
            "-o",
            "ConnectTimeout=10",
            "-o",
            "ServerAliveInterval=30",
        ]
        if self.port != 22:
            opts += ["-p", str(self.port)]
        if self.key_path and os.path.exists(self.key_path):
            opts += ["-i", self.key_path]

        base = ["ssh"] + opts + [f"{self.username}@{self.host}"]
        if self.password and not self.key_path:
            try:
                subprocess.run(["which", "sshpass"], check=True, capture_output=True)
                base = ["sshpass", "-p", self.password] + base
            except subprocess.CalledProcessError:
                print("⚠️ sshpass 미설치: 비밀번호 입력 필요 또는 키 인증 사용 권장")

        base.append(remote_cmd)
        # 공백 포함 인자는 따옴표로 감싸기
        return " ".join(f"'{p}'" if " " in p else p for p in base)

    async def execute_command(self, command: str) -> Dict[str, Any]:
        """SSH 명령어 원격 실행"""
        try:
            cmd = self._build_ssh_command(command)
            proc = await asyncio.create_subprocess_shell(
                cmd, stdout=asyncio.subprocess.PIPE, stderr=asyncio.subprocess.PIPE
            )
            stdout, stderr = await asyncio.wait_for(proc.communicate(), timeout=30)
            return {
                "stdout": stdout.decode(),
                "stderr": stderr.decode(),
                "exit_code": proc.returncode,
                "command": command,
                "timestamp": datetime.now().isoformat(),
            }
        except asyncio.TimeoutError:
            return {"error": "명령어 실행 타임아웃", "command": command}
        except Exception as e:
            return {"error": str(e), "command": command}


# ──────────────────────────
# 안전성 검증
# ──────────────────────────
class SafetyValidator:
    """사전에 지정된 명령어만 허용 + 위험 패턴 차단"""

    def __init__(self, allowed: Dict[str, List[str]]):
        self.allowed = allowed
        self.dangerous = [
            r"rm\s+-rf\s+/",
            r"sudo\s+passwd",
            r"mkfs\.",
            r"dd\s+.*of=/",
            r">\s*/dev/sd",
            r"chmod\s+777\s+/",
            r"chown\s+.*\s+/",
            r"iptables\s+-F",
            r"systemctl\s+(stop|disable)",
            r"(reboot|shutdown|poweroff|halt)",
        ]

    def validate_command(self, category: str, command: str) -> Dict[str, Any]:
        if category not in self.allowed:
            return {"valid": False, "reason": f"허용되지 않은 카테고리: {category}"}
        for pattern in self.dangerous:
            if re.search(pattern, command, re.IGNORECASE):
                return {"valid": False, "reason": "위험한 명령어 패턴 감지"}
        base = command.split()[0]
        if base not in [c.split()[0] for c in self.allowed[category]]:
            return {"valid": False, "reason": f"허용되지 않은 명령어: {base}"}
        return {"valid": True}


# ──────────────────────────
# 메인 도구
# ──────────────────────────
class Tools:
    """Linux & Kubernetes 관리 도구"""

    # ── ① 웹 UI에서 조정할 값 정의 ──
    class Valves(BaseModel):
        SSH_HOST: str = Field("", description="서버 주소")
        SSH_PORT: int = Field(22, description="SSH 포트")
        SSH_USERNAME: str = Field("", description="사용자명")
        SSH_PASSWORD: str = Field("", description="비밀번호(선택)")
        SSH_KEY_PATH: str = Field("", description="키 파일 경로")

        ALLOWED_K8S_COMMANDS: str = Field(
            '["kubectl", "helm"]', description="Kubernetes 명령어"
        )
        ALLOWED_SYSTEM_COMMANDS: str = Field(
            '["ps", "df", "free", "uptime", "top", "who", "last", "netstat"]',
            description="시스템 명령어",
        )
        ALLOWED_DOCKER_COMMANDS: str = Field('["docker"]', description="Docker 명령어")
        COMMAND_TIMEOUT: int = Field(30, description="타임아웃(초)")

    def __init__(self):
        self.valves = self.Valves()
        self.utils = HelpFunctions()
        self.ssh_mgr: SSHManager | None = None
        self._reset_validator()

    # ── ② 안전성 검증기 재생성 ──
    def _reset_validator(self):
        try:
            self.validator = SafetyValidator(
                {
                    "kubernetes": json.loads(self.valves.ALLOWED_K8S_COMMANDS),
                    "system": json.loads(self.valves.ALLOWED_SYSTEM_COMMANDS),
                    "docker": json.loads(self.valves.ALLOWED_DOCKER_COMMANDS),
                }
            )
        except json.JSONDecodeError:
            self.validator = SafetyValidator(
                {
                    "kubernetes": ["kubectl"],
                    "system": ["ps", "df", "free"],
                    "docker": ["docker"],
                }
            )

    # ── ③ SSH 연결 확보 ──
    async def _ensure_ssh(self, emitter) -> bool:
        if not self.valves.SSH_HOST or not self.valves.SSH_USERNAME:
            await emitter.emit_error("SSH 연결 정보가 누락되었습니다")
            return False
        if not self.ssh_mgr:
            self.ssh_mgr = SSHManager(
                self.valves.SSH_HOST,
                self.valves.SSH_PORT,
                self.valves.SSH_USERNAME,
                self.valves.SSH_PASSWORD,
                self.valves.SSH_KEY_PATH,
            )
        if not self.ssh_mgr.connection_tested:
            await emitter.emit("SSH 연결 테스트 중...")
            if not await self.ssh_mgr.test_connection():
                await emitter.emit_error("SSH 연결 실패")
                return False
            await emitter.emit("SSH 연결 성공")
        return True

    # ────────────────────────────────────────────
    # Kubernetes 명령어 실행
    # ────────────────────────────────────────────
    async def execute_kubernetes_command(
        self,
        command_type: str,
        pod_name: str = "",
        namespace: str = "default",
        __event_emitter__: Callable[[dict], Any] | None = None,
    ) -> str:
        emitter = EventEmitter(__event_emitter__)
        if not await self._ensure_ssh(emitter):
            return json.dumps({"error": "SSH 연결 실패"}, ensure_ascii=False)

        pod_name = self.utils.sanitize_input(pod_name)
        namespace = self.utils.sanitize_input(namespace)

        cmd_map = {
            "get_pods": f"kubectl get pods -n {namespace}",
            "get_svcs": f"kubectl get services -n {namespace}",
            "get_nodes": "kubectl get nodes",
            "describe": f"kubectl describe pod {pod_name} -n {namespace}",
            "delete": f"kubectl delete pod {pod_name} -n {namespace}",
            "logs": f"kubectl logs {pod_name} -n {namespace} --tail=100",
            "top": f"kubectl top pods -n {namespace}",
        }
        if command_type not in cmd_map:
            await emitter.emit_error(f"지원되지 않는 타입: {command_type}")
            return json.dumps({"error": "Unsupported type"}, ensure_ascii=False)

        command = cmd_map[command_type]
        valid = self.validator.validate_command("kubernetes", command)
        if not valid["valid"]:
            await emitter.emit_error(valid["reason"])
            return json.dumps({"error": valid["reason"]}, ensure_ascii=False)

        await emitter.emit(f"Kubernetes 명령 실행: {command}")
        result = await self.ssh_mgr.execute_command(command)
        if result.get("error"):
            await emitter.emit_error(result["error"])
        else:
            await emitter.emit_success("명령 실행 완료")
        return json.dumps(result, ensure_ascii=False, indent=2)

    # ────────────────────────────────────────────
    # 시스템 명령어 실행
    # ────────────────────────────────────────────
    async def execute_system_command(
        self,
        command_type: str,
        __event_emitter__: Callable[[dict], Any] | None = None,
    ) -> str:
        emitter = EventEmitter(__event_emitter__)
        if not await self._ensure_ssh(emitter):
            return json.dumps({"error": "SSH 연결 실패"}, ensure_ascii=False)

        cmd_map = {
            "status": "uptime && who",
            "memory": "free -h",
            "disk": "df -h",
            "processes": "ps aux | head -20",
            "network": "netstat -tulnp | head -20",
            "top": "top -b -n1 | head -20",
            "last_login": "last | head -10",
        }
        if command_type not in cmd_map:
            await emitter.emit_error(f"지원되지 않는 타입: {command_type}")
            return json.dumps({"error": "Unsupported type"}, ensure_ascii=False)

        command = cmd_map[command_type]
        valid = self.validator.validate_command("system", command)
        if not valid["valid"]:
            await emitter.emit_error(valid["reason"])
            return json.dumps({"error": valid["reason"]}, ensure_ascii=False)

        await emitter.emit(f"시스템 명령 실행: {command}")
        result = await self.ssh_mgr.execute_command(command)
        if result.get("error"):
            await emitter.emit_error(result["error"])
        else:
            await emitter.emit_success("명령 실행 완료")
        return json.dumps(result, ensure_ascii=False, indent=2)

    # ────────────────────────────────────────────
    # 서버 전반 상태 요약
    # ────────────────────────────────────────────
    async def get_server_overview(
        self,
        __event_emitter__: Callable[[dict], Any] | None = None,
    ) -> str:
        emitter = EventEmitter(__event_emitter__)
        if not await self._ensure_ssh(emitter):
            return json.dumps({"error": "SSH 연결 실패"}, ensure_ascii=False)

        await emitter.emit("서버 상태 수집 중...")
        overview = {
            "timestamp": self.utils.format_datetime(),
            "host": self.valves.SSH_HOST,
            "system": {},
            "kubernetes": {},
            "errors": [],
        }

        # 시스템 정보
        for name, cmd in [
            ("uptime", "uptime"),
            ("memory", "free -h"),
            ("disk", "df -h"),
        ]:
            res = await self.ssh_mgr.execute_command(cmd)
            if res.get("error"):
                overview["errors"].append(res["error"])
            else:
                overview["system"][name] = res["stdout"].strip()

        # Kubernetes 정보
        nodes = await self.ssh_mgr.execute_command("kubectl get nodes 2>/dev/null")
        if not nodes.get("error") and nodes.get("exit_code") == 0:
            overview["kubernetes"]["nodes"] = nodes["stdout"]
            pods = await self.ssh_mgr.execute_command(
                "kubectl get pods --all-namespaces 2>/dev/null"
            )
            if not pods.get("error"):
                overview["kubernetes"]["pods"] = pods["stdout"]
        else:
            overview["kubernetes"]["status"] = "kubectl 사용 불가"

        await emitter.emit_success("수집 완료")
        return json.dumps(overview, ensure_ascii=False, indent=2)

    # ────────────────────────────────────────────
    # SSH 연결 테스트(독립 호출)
    # ────────────────────────────────────────────
    async def test_ssh_connection(
        self,
        __event_emitter__: Callable[[dict], Any] | None = None,
    ) -> str:
        emitter = EventEmitter(__event_emitter__)
        if not self.valves.SSH_HOST or not self.valves.SSH_USERNAME:
            await emitter.emit_error("SSH 연결 정보 없음")
            return json.dumps({"error": "정보 누락"}, ensure_ascii=False)

        await emitter.emit("SSH 연결 테스트 중...")
        tester = SSHManager(
            self.valves.SSH_HOST,
            self.valves.SSH_PORT,
            self.valves.SSH_USERNAME,
            self.valves.SSH_PASSWORD,
            self.valves.SSH_KEY_PATH,
        )
        if await tester.test_connection():
            await emitter.emit_success("SSH 연결 성공")
            return json.dumps({"status": "success"}, ensure_ascii=False)
        await emitter.emit_error("SSH 연결 실패")
        return json.dumps({"error": "연결 실패"}, ensure_ascii=False)


# ──────────────────────────
# 예시 실행 (직접 사용 시)
# ──────────────────────────
if __name__ == "__main__":
    tool = Tools()
